def main():

    reload(sys)
    sys.setdefaultencoding('utf-8')

    outfile = codecs.open('gswatsons_utf8.txt', 'w', 'utf-8')
    outfile.write("##NAME|SUBNAME|ID|TELNUM|NEWADDR|XCOORD|YCOORD@@랄라블라\n")

    #driver = webdriver.Chrome('C:\Python27\chromedriver.exe')

    page = 1
    while True:
        #store_list = getStoresFromBrowser(driver, page)
        #store_list = getStores(page)
        store_list = getStoresMobile(page)
        if store_list == None: break;
        elif len(store_list) == 0: break;

        for store in store_list:
            outfile.write(u'%s|' % store['name'])
            outfile.write(u'%s|' % store['subname'])
            outfile.write(u'%s|' % store['id'])
            outfile.write(u'%s|' % store['pn'])
            outfile.write(u'%s|' % store['newaddr'])
            outfile.write(u'%s|' % store['xcoord'])
            outfile.write(u'%s\n' % store['ycoord'])

        page += 1

        if page == 2: break
        elif len(store_list) < 20: break

        time.sleep(random.uniform(0.3, 0.9))

    outfile.close()

