def main():
    outfile = codecs.open('04_burgerking.txt', 'w', 'utf-8')
    outfile.write("##NAME|BRANCH|ADDR|TELL\n")
    result = []
    for get in url_list:
        result = result + getInfo(get)

    for ssd in result:
        print(ssd)

    for results in result:
        outfile.write(u'%s|' % results['name'])
        outfile.write(u'%s|' % results['branch'])
        outfile.write(u'%s|' % results['addr'])
        outfile.write(u'%s|\n' % results['tell'])
    outfile.close()


def getInfo(url_list):
    url = url_list
    req = urlopen(url)
    response = req.read()
    response_json = json.loads(response)
    entity_list = response_json['body']['storeList']
    dataAll = []

    for ss in entity_list :
        name = "버거킹"
        branch = ss['STOR_NM'].replace(" ","").rstrip().lstrip().upper()
        addr = ss['ADDR_1'].rstrip().lstrip().upper()
        tell = ss['TEL_NO'].rstrip().lstrip().upper()
        dataAll.append({"name":name,"branch":branch,"addr":addr,"tell":tell})
    return dataAll

def errExit(msg):
    sys.stderr.write(msg + '\n')
    sys.exit(0)

if __name__ == '__main__':
    main()
